#!/usr/bin/python
# -*- mode: Python; tab-width: 4; indent-tabs-mode: nil; -*-
# ex: set tabstop=4 :
# Please do not change the two lines above. See PEP 8, PEP 263.
#
"""
    (C) Copyright 2014-2015 Hewlett-Packard Development Company, L.P.

    Monitors a given log file every DELAY seconds and timeouts after
    COUNT checks in which the log has not changed.

    If no log file is given it will monitor the installation log for the supported installers:
    - Red Hat, CentOS and OEL
    - SLES
    - Ubuntu
    - Solaris 10
    - Solaris 11 AI
"""

import os
import sys
import time
from optparse import OptionError
from StringIO import StringIO

from librunplan import PlanOutputHandler

from osprov import platform_util
from osprov.decorators import HandleShowErrorMessage
from osprov.errors import ShowErrorMessage, TimeoutError, NotSupportedError, ProcessExecutionError
from osprov.optparse_ext import OptionParser
from osprov.osbp import ThisTargetServer, logger
from osprov.osbp.constants import MONITORING_STOP_FLAG

# waits CHECK_DELAY * TIMEOUT_COUNT, 10 minutes by default

CHECK_DELAY = 6  # seconds
TIMEOUT_COUNT = 100

LOG = logger.getIt("Monitor Installation")


class MonitorStepOptionsParser(OptionParser):

    """ An options' parser for this step. """

    def defineOptions(self):
        self.add_option("--log", help="the log file that will be monitored")
        self.add_option("--count", default=TIMEOUT_COUNT, type=int,
                        help="the number of times it counts before timing out")
        self.add_option("--delay", default=CHECK_DELAY, type=float,
                        help="the delay in seconds between every log check")

    def validateArgs(self, opt, args):
        if opt.count and opt.count < 0:
            raise OptionError("not a positive number", "count")
        if opt.delay and opt.delay < 0:
            raise OptionError("not a positive number", "delay")


class LogMonitor (object):

    """ Monitors logs until one of the stop files is encountered or times out. """

    def __init__(self, targetServer, logPath, stopFilePaths=(MONITORING_STOP_FLAG,), output=PlanOutputHandler()):
        self.targetServer = targetServer
        self.output = output
        self.logPath = self.targetServer.pathToOGFS(logPath)
        self.stopFilePaths = [self.targetServer.pathToOGFS(path) for path in stopFilePaths]
        self.lastLine = None
        self.checkDelaySeconds = 6
        self.defaultTimeoutCount = 100
        self.timeoutCount = self.defaultTimeoutCount
        self.numberOfReportedLines = 50
        self._cachedLastLines = []
        self.installFailedUserMessage = ""

    def waitForLogFile(self):
        """Waits self.checkDelay * self.timeoutCount for self.logPath to appear."""
        failedCheckCount = 0
        while failedCheckCount < self.timeoutCount:
            if os.path.exists(self.logPath):
                return
            else:
                failedCheckCount += 1
            time.sleep(self.checkDelaySeconds)
        else:
            raise ShowErrorMessage("The install log %s did not appear." % self.logPath)

    def _generateLog(self):
        raise NotImplementedError("abstract")

    def _openLog(self, canGenerate=True):
        """ Returns a file object to self.logPath. """
        return open(self.logPath, "rb")

    def getLastLines(self, count, canGenerate=True):
        """Returns the last lineNo lines of supplied file object."""
        try:
            self._cachedLastLines = self._openLog(canGenerate=canGenerate).readlines()[-count:]
            return self._cachedLastLines
        except IOError, error:
            LOG.error("Failed to read the log file")
            LOG.exception(error)
            self._cachedLastLines = []
            return []

    def getLatestLines(self):
        """ Returns the latest lines from the log file."""
        lastLines = self.getLastLines(self.numberOfReportedLines)
        if not lastLines:
            return []
        if self.lastLine is None:
            self.lastLine = lastLines[-1]
            return lastLines
        else:
            curLastLine = lastLines[-1]
            if curLastLine == self.lastLine:
                return []
            else:
                self.lastLine = curLastLine
                return lastLines

    def isInstallDone(self):
        """ Returns True if the installation has finished. """
        for path in self.stopFilePaths:
            if os.path.exists(path):
                return True
        else:
            return False

    def isInstallFailed(self):
        """
        Returns True if the installation is known to have failed.
        To be implemented by subclasses if needed.
        """

        return False

    def adjustTimeout(self):
        """Hook for dynamically adjusting the timeout. Gets called at each iteration. """

        pass

    def main(self):
        """ Main monitoring function """
        print("Monitoring %s log file" % self.logPath)

        print("Waiting for %s to appear" % self.logPath)
        self.waitForLogFile()
        print("Found the log file")

        failedCheckCount = 0
        while failedCheckCount < self.timeoutCount and not self.isInstallDone() and not self.isInstallFailed():
            lastLines = self.getLatestLines()
            if lastLines:
                failedCheckCount = 0
                self.output.replaceMessages("Monitoring Installation", "".join(lastLines))
            else:
                failedCheckCount += 1
            self.adjustTimeout()
            time.sleep(self.checkDelaySeconds)
        else:
            print("Here are the last %d lines of the log:" % self.numberOfReportedLines)
            print("".join(self.getLastLines(self.numberOfReportedLines, canGenerate=False)))
            if self.isInstallDone():
                print("Installation completed.")
                return 0
            elif self.isInstallFailed():
                raise ShowErrorMessage(
                    "The installation failed: %s" % self.installFailedUserMessage)
            else:
                raise TimeoutError("Monitoring the log file timed out.")


class RPMBasedInstallerLogMonitor(LogMonitor):

    def __init__(self, targetServer, logPath, stopFilePaths=(MONITORING_STOP_FLAG,), output=PlanOutputHandler(), rpmArguments=""):
        LogMonitor.__init__(self, targetServer, logPath, stopFilePaths, output)
        self._rpmArguments = rpmArguments

    def _openLog(self, canGenerate=True):
        if canGenerate:
            generatedLog = self._generateLog()
            if generatedLog.strip():
                return StringIO(generatedLog)
        return open(self.logPath, "rb")

    def _generateLog(self):
        try:
            return self.targetServer.rosh().getStdout(
                "\"rpm {rpmArguments} -qa --queryformat '{queryFormat}' | sort -n | tail -n 50 \"".format(
                    rpmArguments=self._rpmArguments,
                    queryFormat="(%{installtime:date}) Installed rpm: %{name}\n"
                ),
            )
        except ProcessExecutionError, e:
            LOG.exception(e)
            return ""


class Rhel5InstallationLogMonitor(LogMonitor):

    def __init__(self, targetServer):
        LogMonitor.__init__(
            self, targetServer,
            logPath="/tmp/anaconda.log",
            stopFilePaths=(
                os.path.join("/mnt/sysimage/tmp", os.path.basename(MONITORING_STOP_FLAG)),
                MONITORING_STOP_FLAG
            )
        )


class Rhel6InstallationLogMonitor(RPMBasedInstallerLogMonitor):

    def __init__(self, targetServer):
        RPMBasedInstallerLogMonitor.__init__(
            self, targetServer,
            logPath="/tmp/anaconda.log",
            stopFilePaths=(
                os.path.join("/mnt/sysimage/tmp", os.path.basename(MONITORING_STOP_FLAG)),
                MONITORING_STOP_FLAG
            )
        )


class Rhel7InstallationLogMonitor(LogMonitor):

    def __init__(self, targetServer):
        LogMonitor.__init__(
            self, targetServer,
            logPath="/tmp/packaging.log",
            stopFilePaths=(
                os.path.join("/mnt/sysimage/tmp", os.path.basename(MONITORING_STOP_FLAG)),
                MONITORING_STOP_FLAG,
            )
        )


class SlesInstallationLogMonitor(RPMBasedInstallerLogMonitor):
    def __init__(self, targetServer):
        RPMBasedInstallerLogMonitor.__init__(
            self, targetServer,
            logPath="/var/log/YaST2/y2log",
            stopFilePaths=(
                os.path.join("/mnt/tmp", os.path.basename(MONITORING_STOP_FLAG)),
                MONITORING_STOP_FLAG
            ),
            rpmArguments="--dbpath /mnt/var/lib/rpm"
        )


class UbuntuInstallationLogMonitor(LogMonitor):

    def __init__(self, targetServer):
        LogMonitor.__init__(self, targetServer, "/var/log/syslog")


class Solaris11InstallationMonitor(LogMonitor):

    def __init__(self, targetServer):
        LogMonitor.__init__(self, targetServer, "/system/volatile/install_log")
        self.installDoneMessage = "automated installation succeeded"
        self.installFailedMessage = "automated installation failed"
        self.installFailedUserMessage = "The install log reported failure."
        self._installPhaseStartedMessage = "install phase ... started"
        self._installPhaseDoneMessage = "install phase ... done"

    def isInstallDone(self):
        """ Returns True if the installation has finished. """
        return self.installDoneMessage in "\n".join(self._cachedLastLines).lower()

    def isInstallFailed(self):
        """Return True if the install log contains a failure message."""
        return self.installFailedMessage in "\n".join(self._cachedLastLines).lower()

    def adjustTimeout(self):
        """Sets a larger timeout after the 'Install Phase ... Started' message is detected in the log."""
        if self._installPhaseStartedMessage in "\n".join(self._cachedLastLines).lower():
            self.timeoutCount = 600
        if self._installPhaseDoneMessage in "\n".join(self._cachedLastLines).lower():
            self.timeoutCount = self.defaultTimeoutCount


class Solaris10InstallationMonitor(LogMonitor):

    def __init__(self, targetServer):
        LogMonitor.__init__(self, targetServer, "/a/var/sadm/system/logs/install_log")
        self.installFailedMessage = "installation: failed"
        self.installFailedUserMessage = "The install log reported failure."

    def isInstallFailed(self):
        """Return True if the install log contains a failure message."""
        return self.installFailedMessage in "\n".join(self._cachedLastLines).lower()


def factorLogMonitor(targetServer):
    """ Return a LogMonitor based on the reported OS version"""
    osVersion = targetServer.getServerVO().getOsVersion().lower()
    if platform_util.isSLES(osVersion):
        return SlesInstallationLogMonitor(targetServer)
    elif platform_util.isRHELCompatible(osVersion, version=5):
        return Rhel5InstallationLogMonitor(targetServer)
    elif platform_util.isRHELCompatible(osVersion, version=6):
        return Rhel6InstallationLogMonitor(targetServer)
    elif platform_util.isRHELCompatible(osVersion, version=7):
        return Rhel7InstallationLogMonitor(targetServer)
    elif platform_util.isUbuntu(osVersion):
        return UbuntuInstallationLogMonitor(targetServer)
    elif platform_util.isSolaris(osVersion, version=11):
        return Solaris11InstallationMonitor(targetServer)
    elif platform_util.isSolaris(osVersion, version=10):
        return Solaris10InstallationMonitor(targetServer)
    raise NotSupportedError("Monitoring installation for %s is not supported" % osVersion)


@HandleShowErrorMessage("monitor installation", LOG)
def main():
    options, remainingArgs = MonitorStepOptionsParser().parse_args()

    targetServer = ThisTargetServer()

    logFile = options.log
    if not logFile:
        logMonitor = factorLogMonitor(targetServer)
    else:
        logMonitor = LogMonitor(targetServer, logFile)
    logMonitor.checkDelaySeconds = options.delay
    logMonitor.timeoutCount = options.count

    return logMonitor.main()


if __name__ == "__main__":
    sys.exit(main())

#!/usr/bin/python
# -*- mode: Python; tab-width: 4; indent-tabs-mode: nil; -*-
# lint_ignore=E501,I0011,C0301,E501
# pylint: disable=C0301
# ex: set tabstop=4 :
# Please do not change the two lines above. See PEP 8, PEP 263.
"""
    (C) Copyright 2013-2015 Hewlett-Packard Development Company, L.P.
"""

WAIT_AT_MOST = 15

DEFAULT_WAIT_TIME_SHORT = 5
DEFAULT_WAIT_TIME_LONG = 20
# The first and last x seconds are called hot edges because we check more often during those periods
HOT_EDGE_DURATION = 3 * 60

import sys
import time

sys.path.append("/opsw/apx/runtime/script/osprov.run_os_build_plan_papx/")
sys.path.append("/opt/opsware/pylibs2/")

from osprov import boot
from osprov.util import NullLog
from osprov.server.sa import State
from osprov.osbp import ThisTargetServer, logger
from osprov.decorators import HandleShowErrorMessage
from osprov.errors import ShowErrorMessage, NotSupportedError
from osprov.optparse_ext import OptionParser, OptionValueError

LOG = logger.getIt("wait_for_agent")


class ServerValidationError(ShowErrorMessage):
    """ Raised locally when server is not yet in a good state """
    pass


class WaitSomeMore(ShowErrorMessage):
    """ Raised locally to show that the server is good, but we need to wait some more """


class WaitForAgentParser(OptionParser):
    """ Parse the arguments of the step"""

    def defineOptions(self):

        self.add_option("--ogfs", "--maintenance",
                        action="store_true", dest="expectMaintenance",
                        help="indicates we are expecting a maintenance (ogfs) agent")
        self.add_option("--production",
                        action="store_false", dest="expectMaintenance",
                        help="indicates we are expecting a production agent")
        self.add_option("--atLeast",
                        type="int", metavar="MINUTES", default=None,
                        help="the number of minutes to wait before actively checking \
                    for the agent; if not specified an optimized strategy is used")
        self.add_option("--atMost",
                        type="int", metavar="MINUTES", default=WAIT_AT_MOST,
                        help="the maximum number of minutes to wait for the agent to \
                    come back online; default value: %default minute(s)")
        self.add_option("--reportingAtLeast",
                        type="int", metavar="MINUTES", default=None,
                        help="the number of minutes the agent must continuously be reporting")
        # deprecated, left here for backwards compatibility
        self.add_option("--ignoreServiceOS",
                        default=False, action="store_true", dest="ignoreServiceOS",
                        help="(DEPRECATED) disables a check for the correct Service OS")

    def validateArgs(self, options, remainingArgs):
        # cannot call super because OptionParser is not a new-style class
        OptionParser.validateArgs(self, options, remainingArgs)
        if options.atLeast is not None:
            if options.atLeast < 1:
                raise OptionValueError("atLeast cannot be less than 1 (minute)")
            if options.atLeast >= options.atMost:
                raise OptionValueError("atLeast must be smaller than atMost (%d and %d)" %
                                      (options.atLeast, options.atMost))
        if options.atMost < 1:
            raise OptionValueError("atMost cannot be less than 1 (minute)")

        if options.reportingAtLeast is not None:
            if options.reportingAtLeast < 1:
                raise OptionValueError("reportingAtLeast cannot be less than 1 (minute)")
        if options.reportingAtLeast:
            options.reportingAtLeast *= 60
        if options.atLeast:
            options.atLeast *= 60
        options.atMost *= 60


class AgentWaiter(object):
    """Wait for the agent to become available in the desired mode (maintenance or production)"""

    def __init__(self, options, log=NullLog()):

        self.options = options
        self.waitTime = DEFAULT_WAIT_TIME_SHORT
        self.log = log
        self.targetServer = None
        self.startTime = None
        self.deadLine = None
        self.reportingStartTime = None

    def wait(self):
        """Sleep for the required amount of time, based on internal state."""
        self.log.debug("Will sleep for %d seconds, been waiting for %s seconds", self.waitTime, time.time() - self.startTime)
        time.sleep(self.waitTime)

    def setTiming(self):
        """Set timing rules for regular wait."""
        if self.targetServer.isAgentListening():
            self.waitTime = DEFAULT_WAIT_TIME_SHORT
        else:
            waitingFor = time.time() - self.startTime
            timeToDeadline = self.deadLine - time.time()
            if waitingFor <= HOT_EDGE_DURATION or timeToDeadline <= HOT_EDGE_DURATION:
                self.waitTime = DEFAULT_WAIT_TIME_SHORT
            else:
                self.waitTime = DEFAULT_WAIT_TIME_LONG

    def renewTargetServer(self):
        """Renew self.targetServer if present, or create new instance if not."""
        if self.targetServer is None:
            self.targetServer = ThisTargetServer()
        else:
            self.targetServer = self.targetServer.renew()
        self.log.debug("Renewed target server is: %s", self.targetServer)

    def recoverUnreachable(self):
        """Update the TargetServer state when we know better."""
        if self.targetServer.serverVO.state != State.UNREACHABLE:
            self.log.debug("Server is %s, not unreachable, no need to recover it", self.targetServer.serverVO.state)
            return
        if not self.targetServer.isProduction():
            # if no agent service was found (not production) we must be in maintenance
            targetState = State.MAINTENANCE
        elif not self.targetServer.isAgentless():
            # Only recover to OK if the server is managed
            targetState = State.OK
        else:
            return
        self.log.debug("Recovering server from UNREACHABLE state")
        try:
            self.targetServer.updateState(state=targetState)
        except:  # pylint: disable=W0702
            # just except, pytwist doesn't raise proper error QC: 143883
            self.log.exception("Failed to update device state")
            raise NotSupportedError("Failed to update server state")

    def validateServerState(self):
        """Verify that the server is in the required state when the agent is accessible."""
        self.renewTargetServer()
        if self.options.expectMaintenance:
            if not self.targetServer.isMaintenance():
                raise ServerValidationError(
                    "The target server did not start in MAINTENANCE but was expected to."
                )
        else:
            if self.targetServer.isAgentless() or self.targetServer.isMaintenance():
                # the ogfs agent is "agentless" because there is no management path
                raise ServerValidationError(
                    "The target server is in MAINTENANCE but was not expected to."
                )

    def validateReportingAtLeast(self):
        if not self.options.reportingAtLeast:
            return
        now = time.time()
        if not self.reportingStartTime:
            self.reportingStartTime = now
        reportingPeriod = now - self.reportingStartTime
        if reportingPeriod < self.options.reportingAtLeast:
            raise WaitSomeMore(
                "The target server was not reporting correctly for at least %s minutes, only %s." % (
                    int(self.options.reportingAtLeast / 60), int(reportingPeriod / 60)
                )
            )

    def validateAgentAccessible(self):
        if not self.targetServer.isAgentListening():
            raise ServerValidationError("The agent did not appear on the network (is not listening on its port).")
        if not self.targetServer.isAccessible():
            # isAccessible instead of hasRosh access, because for some target servers
            # this will just yield no
            raise ServerValidationError("The agent does not have working remote shell capability")
        self.log.debug("Agent is accessible")

    def waitUpfront(self):
        """ Wait before checking for the agent if --atLeast """
        if self.options.atLeast is not None:
            print "Sleeping for %d minutes before checking for agent" % (self.options.atLeast / 60)
            time.sleep(self.options.atLeast)

    def needToWait(self):
        """ Do we still need to wait ?  """
        return time.time() < self.deadLine

    def run(self):
        """Start waiting for the agent."""
        self.startTime = time.time()
        self.deadLine = self.startTime + self.options.atMost

        if self.options.ignoreServiceOS:
            print "Ignoring deprecated option: ignoreServiceOS"

        self.waitUpfront()

        if self.options.expectMaintenance:
            print "Waiting for registration of agent running on maintenance OS"
        else:
            print "Waiting for registration of agent running on production OS"

        # Main wait loop
        error = None
        while self.needToWait():
            try:
                self.renewTargetServer()
                # The validation types, order important !
                self.validateAgentAccessible()
                self.recoverUnreachable()
                self.validateServerState()
                self.validateReportingAtLeast()

                if self.options.expectMaintenance:
                    print "Maintenance agent is accessible"
                else:
                    print "Production agent is accessible"
                break
            except (NotSupportedError, ServerValidationError) as why:
                error = why
                self.reportingStartTime = None
                self.log.debug("Agent is not yet accessible exception follows, will keep on waiting")
                self.log.exception(error)
            except WaitSomeMore, why:
                error = why

            self.setTiming()

            self.wait()
        else:
            if error is not None:
                raise error  # pylint: disable=E0702
            else:
                raise ShowErrorMessage("unknown error while waiting for agent")


@HandleShowErrorMessage("wait for the HP SA Agent", log=LOG)
def main():
    options, args = WaitForAgentParser(noRemainingArgs=True).parse_args()
    del args
    try:
        AgentWaiter(options, log=LOG).run()
    except ShowErrorMessage, e:
        bootController = boot.getPreviouslyUsedBootController(ThisTargetServer())
        if bootController is not None:
            log = bootController.getLog()
            if log:
                raise e.__class__("%s\nAdditional information from boot control:\n%s" % (str(e), log), e.exitCode)
        raise e

if __name__ == "__main__":
    sys.exit(main())

