class LogMonitor (object):

    """ Monitors logs until one of the stop files is encountered or times out. """

    def __init__(self, targetServer, logPath, stopFilePaths=(MONITORING_STOP_FLAG,), output=PlanOutputHandler()):
        self.targetServer = targetServer
        self.output = output
        self.logPath = self.targetServer.pathToOGFS(logPath)
        self.stopFilePaths = [self.targetServer.pathToOGFS(path) for path in stopFilePaths]
        self.lastLine = None
        self.checkDelaySeconds = 6
        self.defaultTimeoutCount = 100
        self.timeoutCount = self.defaultTimeoutCount
        self.numberOfReportedLines = 50
        self._cachedLastLines = []
        self.installFailedUserMessage = ""

    def waitForLogFile(self):
        """Waits self.checkDelay * self.timeoutCount for self.logPath to appear."""
        failedCheckCount = 0
        while failedCheckCount < self.timeoutCount:
            if os.path.exists(self.logPath):
                return
            else:
                failedCheckCount += 1
            time.sleep(self.checkDelaySeconds)
        else:
            raise ShowErrorMessage("The install log %s did not appear." % self.logPath)

    def _generateLog(self):
        raise NotImplementedError("abstract")

    def _openLog(self, canGenerate=True):
        """ Returns a file object to self.logPath. """
