from osprov.wrappers.hponcfg import HpOnCfg, GetConfigOperation
from osprov.decorators import HandleShowErrorMessage


log = logger.getIt("Print iLO Configuration")


class PrintILOConfigOptionParser(OptionParser):

    def __init__(self, *args, **kwargs):
        OptionParser.__init__(self, *args, **kwargs)
        self.usage = "Prints iLo configuration information."

    def defineOptions(self):
        self.add_option("--print",
                        default="config",
                        choices=["mgmtIP", "mgmtPortIP", "config"],
                        action="store", dest="print_option",
                        help="Prints iLO configuration information. "
                             "Possible values: mgmtIP, mgmtPortIP or config\n"
                             "mgmtIP      print all management IPs of the iLO\n"
                             "mgmtPortIP  print port and all management IPs of the iLO\n"
                             "config      print iLo configuration\n"
                        )


def getPreferredILOAddresses(iloIPs, targetServer):
    if len(iloIPs):
        if targetServer.isIPv6Managed():
            iloIPs = sorted(iloIPs, key=lambda x: isinstance(x, ipaddr.IPv4Address))
        else:
            iloIPs = sorted(iloIPs, key=lambda x: isinstance(x, ipaddr.IPv6Address))
        return " ".join([str(x) for x in iloIPs])
    else:
        raise ShowErrorMessage("Cannot determine any iLO IP addresses")


def getPreferredILOPort(port):
    if not port:
        port = 443
        log.warn("Cannot determine port, defaulting to 443")
    return port


