def main():
    targetServer = ThisTargetServer()
    parser = PrintILOConfigOptionParser()
    options, remaining_args = parser.parse_args()
    del remaining_args

    hpOnCfg = HpOnCfg(targetServer, log=log)
    if options.print_option == "mgmtIP":
        print getPreferredILOAddresses(hpOnCfg.getIloAddresses(), targetServer)
    elif options.print_option == "mgmtPortIP":
        print "%s %s" % (getPreferredILOPort(hpOnCfg.getIloPort()), getPreferredILOAddresses(hpOnCfg.getIloAddresses(), targetServer))
    else:
        operation = GetConfigOperation(hpOnCfg)
        result = operation.run()
        print result

    return 0

if __name__ == "__main__":
    sys.exit(main())

