import sys

from osprov          import ilo
from osprov.errors   import TimeoutError, SetupError, NotSupportedError
from osprov.osbp     import ThisTargetServer
from osprov.osbp     import logger
from osprov.patterns.scanner import TimeoutScanner, StopScan
from osprov.optparse_ext     import OptionParser, OptionValueError
from osprov.decorators       import HandleShowErrorMessage

LOG = logger.getIt("check iLO4 or above")

class CheckForILOServiceOptparser(OptionParser):
	""" Custom option parser """

	def defineOptions(self):
		self.prog  =  "Check For iLO Service and validate iLO4 or above"
		self.set_usage("%s [options]" % self.prog)
		self.add_option("-t", "--atMost", type = "int", default = "10", metavar="MINUTES",
			help = "Wait at most this many minutes before timing out. Default %default."
		)

	def validateArgs(self, opt, args):
		if opt.atMost < 1:
			raise OptionValueError("MINUTES should be more than 1.")

class CheckForILOServiceScanner(TimeoutScanner):
	""" Custom scanner for the iLO service """

	def __init__(self, targetServer, *args, **kwargs):
		super(CheckForILOServiceScanner, self).__init__(*args, **kwargs)
		self.targetServer = targetServer

	def shortcut(self):
		if self.targetServer.serverVO.manufacturer:
			if self.targetServer.serverVO.manufacturer.strip().lower() not in (
					"hp" ,
					"hewlett-packard",
					"hewlett-packard company",
					"hewlett-packard inc.",
					"hewlett packard",
					"hewlett packard company",
					"hewlett packard inc.",
				):
				raise NotSupportedError("Server model %s, manufacturer %s does not support iLO" % (
					self.targetServer.serverVO.model,
					self.targetServer.serverVO.manufacturer,
				))
		self.scan()

	def scan(self):
		self.log.debug("Scanning for iLO")
		try:
			iloServ = ilo.getIlo(self.targetServer, log = self.log)
			self.log.debug("Found iLO %s" % iloServ)
			print "Service active, version %s." % iloServ.version()
			if iloServ.version().lower() not in (
					"ilo4", "ilo4_rest"
				):
				raise NotSupportedError("iLO version %s is not supported. iLO 4 or above required.\nServer model %s" % (
					iloServ.version(),
					self.targetServer.serverVO.model,
				))
			raise StopScan()
		except SetupError, why:
			self.log.exception(why)


@HandleShowErrorMessage("check iLO service and version", LOG)
def main():
	""" Main entry point """
	opt, args = CheckForILOServiceOptparser().parse_args()
	if args:
		print "Warning, unneeded arguments will be ignored: %s" % (" ".join(args))
	scanner = CheckForILOServiceScanner(
		targetServer =  ThisTargetServer(),
		timeoutMin     = opt.atMost,
		waitUpfrontMin = 1,
		log = LOG
	)
	try:
		scanner()
	except TimeoutError, why:
		LOG.exception(why)
		print "Error: The iLO service did not  become available after %d minutes." % opt.atMost
		print "This Build Plan requires the iLO service and can't continue without it."
		return 10


if __name__ == "__main__" :
	sys.exit(main())
